<?php

namespace CodeBlog\Thumb;

/**
 * Class CodeBlog Thumb
 *
 * @author Whallysson Avelino <https://github.com/whallysson>
 * @package CodeBlog\Thumb
 *
 * How to use:
 * Ex.: http://seusite.com/upload/images/imagem.png?w=250&h=500&a=b&zc=2
 * $Tim = new Thumb('images_cache');
 * echo $Tim->imgCreate(http://seusite.com/upload/images/imagem.png?w=250&h=500&a=b&zc=2);
 * or
 * echo $Tim->imgCreate(upload/images/imagem.png?w=250&h=500&a=b&zc=2);
 */
class Thumb
{
    /** @var string */
    private $imagePath;

    /** @var string */
    private $imageName;

    /** @var string */
    private $imageMime;

    /** @var string */
    private $imageInfo;

    /** @var string */
    private $file;

    /** @var string */
    private $url;

    /** @var int|null|string */
    private $time = 604800; // 7 days (default)

    /** @var string */
    private $image;

    /** @var string */
    private $result;

    /** @var string */
    private $error;

    /** @var string */
    private $cacheFolder;

    /** PROPERTIES */

    /** @var string */
    private $wbase = 800; // Default image width size if not set

    /** @var int */
    private $width;

    /** @var int */
    private $height;

    /** @var int */
    private $zc;

    /** @var int */
    private $a;

    /** QUALITY */

    /** @var int */
    private $jpgQuality = 75; // 0 a 100

    /** @var int|null */
    private $pngCompressor = 5; // floor(95 * 0.09)

    /**
     * Allow jpg and png to thumb and cache generate
     *
     * @var array allowed media types
     */
    private static $allowedExt = ['image/jpeg', "image/png"];

    /**
     * Thumb constructor.
     * @param string $cacheFolder
     * @param null|string $Time
     * @param int|null $jpgQuality
     * @param int|null $pngCompressor
     * @throws \Exception
     */
    function __construct(
        string $cacheFolder,
        ?string $Time = null,
        ?int $jpgQuality = null,
        ?int $pngCompressor = null
    ) {
        $this->cacheFolder = $cacheFolder;
        $this->time = ($Time ? $Time : $this->time);

        $this->jpgQuality = $jpgQuality ? $jpgQuality : $this->jpgQuality;
        $this->pngCompressor = $pngCompressor ? $pngCompressor : $this->pngCompressor;

        if (!file_exists($this->cacheFolder) || !is_dir($this->cacheFolder)) {
            if (!mkdir($this->cacheFolder, 0755, true)) {
                throw new \Exception("Could not create cache folder");
            }
        }

        $this->existeFileCache();
    }

    /**
     * @param string $imagePath
     * @return null|string
     */
    public function imgCreate(string $imagePath): ?string
    {
        $this->url = $imagePath;

        $ParseUrl = parse_url($this->url);
        $this->file['query'] = (isset($ParseUrl['query']) ? $ParseUrl['query'] : null);

        $Exp = explode('.', substr(strrchr($ParseUrl['path'], '/'), 1));

        $this->file['ext'] = (isset($Exp[1]) ? $Exp[1] : null);

        if (!file_exists($ParseUrl['path'])) {
            return "Image not found";
        }

        $this->imagePath = $ParseUrl['path'];
        $this->imageMime = mime_content_type($this->imagePath);
        $this->imageInfo = pathinfo($this->imagePath);

        if (!in_array($this->imageMime, self::$allowedExt)) {
            return "Not a valid JPG or PNG image";
        }

        $this->imageName = $this->name($this->imagePath);
        if (file_exists("{$this->cacheFolder}/{$this->imageName}") && is_file("{$this->cacheFolder}/{$this->imageName}")) {
            return "{$this->cacheFolder}/{$this->imageName}";
        }

        /** If the image does not exist it creates in the image in the cache folder */
        $this->noImage();

        /** Clean the Cache */
        $this->cleanCache();

        if (file_exists("{$this->cacheFolder}/{$this->imageName}") && is_file("{$this->cacheFolder}/{$this->imageName}")) {
            /** Return the cached image if it already exists */
            return "{$this->cacheFolder}/{$this->imageName}";
        } else {
            $response = $this->setProps();
            if (!empty($response)) {
                return $response;
            } else {
                /** If the image does not exist it creates in the image in the cache folder */
                $this->noImage('no_image');
            }
        }

        return null;
    }

    /**
     * Clear cache
     *
     * @param string|null $imagePath
     * @example $t->flush("images/image.jpg"); clear image name and variations size
     * @example $t->flush(); clear all image cache folder
     */
    public function flush(string $imagePath = null): void
    {
        foreach (scandir($this->cacheFolder) as $file) {
            $file = "{$this->cacheFolder}/{$file}";
            if ($imagePath && strpos($file, $this->hash($imagePath))) {
                $this->imageDestroy($file);
            } elseif (!$imagePath) {
                $this->imageDestroy($file);
            }
        }
    }

    /**
     * @return mixed
     */
    public function getResult()
    {
        return $this->result;
    }

    /**
     * @return mixed
     */
    public function getError()
    {
        return $this->error;
    }


    private function existeFileCache(): void
    {
        $folderFileName = "{$this->cacheFolder}/cacheTime.txt";
        if (!file_exists($folderFileName)) {
            $SiteMapCheck = fopen($folderFileName, "w+");
            fwrite($SiteMapCheck, date('Y-m-d'));
            fclose($SiteMapCheck);
        }
    }


    private function cleanCache(): void
    {
        $this->existeFileCache();

        $CacheTime = "{$this->cacheFolder}/cacheTime.txt";
        if (filemtime($CacheTime) < (time() - $this->time)) {
            $files = glob("{$this->cacheFolder}/*");
            if ($files) {
                $timeAgo = time() - $this->time;
                foreach ($files as $file) {
                    if (filemtime($file) < $timeAgo) {
                        unlink($file);
                    }
                }

                clearstatcache();
            }
        }
    }

    /**
     * @param string $imagePatch
     */
    private function imageDestroy(string $imagePatch): void
    {
        if (file_exists($imagePatch) && is_file($imagePatch)) {
            unlink($imagePatch);
        }
    }


    /**
     * @param string $name
     * @return string
     */
    protected function name(string $name, int $width = null, int $height = null): string
    {
        $filterName = filter_var(mb_strtolower(pathinfo($name)["filename"]), FILTER_SANITIZE_STRIPPED);
        $formats = 'ÀÁÂÃÄÅÆÇÈÉÊËÌÍÎÏÐÑÒÓÔÕÖØÙÚÛÜüÝÞßàáâãäåæçèéêëìíîïðñòóôõöøùúûýýþÿRr"!@#$%&*()_-+={[}]/?;:.,\\\'<>°ºª';
        $replace = 'aaaaaaaceeeeiiiidnoooooouuuuuybsaaaaaaaceeeeiiiidnoooooouuuyybyRr                                 ';
        $trimName = trim(strtr(utf8_decode($filterName), utf8_decode($formats), $replace));
        $name = str_replace(["-----", "----", "---", "--"], "-", str_replace(" ", "-", $trimName));

        $hash = $this->hash($this->imagePath);
        $ext = ($this->imageMime == "image/jpeg" ? ".jpg" : ".png");
        $widthName = ($width ? "-{$width}" : "");
        $heightName = ($height ? "x{$height}" : "");

        return "{$name}{$widthName}{$heightName}-{$hash}{$ext}";
    }

    /**
     * @param string $path
     * @return string
     */
    protected function hash(string $path): string
    {
        return hash("crc32", pathinfo($path)['basename']);
    }


    /**
     * UPLOADER
     */
    private function setProps()
    {
        $this->properties();
        $this->imgSize();

        if (strstr($this->file["type"], 'image/')) {
            $this->uploadImage();
            return $this->getResult();
        }

        return null;
    }

    private function noImage(string $no = null): void
    {
        if (empty($this->imageName) || $no) {
            $fileName = 'no_image.jpg';
            $folderFileName = "{$this->cacheFolder}/{$fileName}";
            if (!file_exists($folderFileName)) {
                $ImgData = base64_decode("");
                $file = fopen($folderFileName, 'w+');
                fwrite($file, $ImgData);
                fclose($file);

                $time = time() + 31536000;
                touch($folderFileName, $time);
            }

            $this->url = $folderFileName . (!empty($this->file['query']) ? "?{$this->file['query']}" : null);
            $ParseUrl = parse_url($this->url);
            $this->imageName = substr(strrchr($ParseUrl['path'], '/'), 1);
            $this->file['ext'] = mb_strtolower(strrchr($this->imageName, '.'));
        }
    }


    private function imgSize(): void
    {
        $Src = (strrpos($this->url, '?') ? substr($this->url, 0, strrpos($this->url, '?')) : $this->url);
        $Tam = getimagesize($Src);
        $this->file['type'] = $Tam['mime'];
        $this->file['src'] = $Src;

        $x = $Tam[0];
        $y = $Tam[1];

        /** Leaves the image in proportion to the width */
        if (!empty($this->width)) {
            $this->width = $this->width;
            $this->height = (!empty($this->height) ? $this->height : ($this->width * $y) / $x);
        } elseif (empty($this->width) && !empty($this->height)) {
            /** Leaves the image in proportion to the height */
            $this->height = $this->height;
            $this->width = ($this->height * $x) / $y;
        } else {
            /** Leaves the image in the proportions set at the base (800px width) */
            $this->width = ($this->wbase < $x ? $this->wbase : $x);
            $this->height = ($this->width * $y) / $x;
        }
    }


    private function properties(): void
    {
        parse_str($this->file['query'], $prop);
        $this->width = ((int)isset($prop['w']) ? $prop['w'] : null);
        $this->height = ((int)isset($prop['h']) ? $prop['h'] : null);
        $this->zc = ((int)isset($prop['zc']) ? $prop['zc'] : 1);
        $this->a = (isset($prop['a']) ? $prop['a'] : 'c');
    }


    protected function uploadImage(): void
    {
        $Transparency = null;
        switch ($this->file['type']):
            case 'image/jpg':
            case 'image/jpeg':
            case 'image/pjpeg':
                $this->image = imagecreatefromjpeg($this->file['src']);
                break;
            case 'image/png':
            case 'image/x-png':
                $this->image = imagecreatefrompng($this->file['src']);
                $Transparency = 1;
                break;
            default :
                $this->image = null;
                break;
        endswitch;

        if (!$this->image):
            $this->result = false;
            $this->error = 'Tipo de arquivo inválido, envie imagens JPG ou PNG!';
        else:
            $x = imagesx($this->image);
            $y = imagesy($this->image);
            $ImageX = $this->width;
            $ImageH = $this->height;
            $origin_x = 0;
            $origin_y = 0;

            /** Reduce and add borders */
            if ($this->zc == 3) {
                $FinalHeight = $y * ($ImageX / $x);

                if ($FinalHeight > $ImageH) {
                    $ImageX = $x * ($ImageH / $y);
                } else {
                    $ImageH = $FinalHeight;
                }
            }

            $NewImage = imagecreatetruecolor($ImageX, $ImageH);
            imagealphablending($NewImage, false);

            /** Checks whether the "Transparency" variable is true, to make the background transparent */
            if (!empty($Transparency)):
                $colorTransparent = imagecolorallocatealpha($NewImage, 0, 0, 0, 127);
                imagefill($NewImage, 0, 0, $colorTransparent);
            else:
                $white = imagecolorallocate($NewImage, 255, 255, 255);
                imagefill($NewImage, 0, 0, $white);
            endif;

            imagesavealpha($NewImage, true);

            /** scale down and add borders */
            if ($this->zc == 2) {
                $FinalHeight = $y * ($ImageX / $x);

                if ($FinalHeight > $ImageH) {
                    $origin_x = $ImageX / 2;
                    $ImageX = $x * ($ImageH / $y);
                    $origin_x = round($origin_x - ($ImageX / 2));
                } else {
                    $origin_y = $ImageH / 2;
                    $ImageH = $FinalHeight;
                    $origin_y = round($origin_y - ($ImageH / 2));
                }
            }

            if ($this->zc > 0) {
                $src_x = $src_y = 0;
                $src_w = $x;
                $src_h = $y;

                $cmp_x = $x / $ImageX;
                $cmp_y = $y / $ImageH;

                /** calculate x or y coordinate and width or height of source */
                if ($cmp_x > $cmp_y) {
                    $src_w = round($x / $cmp_x * $cmp_y);
                    $src_x = round(($x - ($x / $cmp_x * $cmp_y)) / 2);
                } else {
                    if ($cmp_y > $cmp_x) {
                        $src_h = round($y / $cmp_y * $cmp_x);
                        $src_y = round(($y - ($y / $cmp_y * $cmp_x)) / 2);
                    }
                }

                /** positional cropping! */
                if ($this->a) {
                    if (strpos($this->a, 't') !== false) {
                        $src_y = 0;
                    }
                    if (strpos($this->a, 'b') !== false) {
                        $src_y = $y - $src_h;
                    }
                    if (strpos($this->a, 'l') !== false) {
                        $src_x = 0;
                    }
                    if (strpos($this->a, 'r') !== false) {
                        $src_x = $x - $src_w;
                    }
                }

                imagecopyresampled($NewImage, $this->image, $origin_x, $origin_y, $src_x, $src_y, $ImageX, $ImageH,
                    $src_w, $src_h);
            } else {
                imagecopyresampled($NewImage, $this->image, 0, 0, 0, 0, $ImageX, $ImageH, $x, $y);
            }


            switch ($this->file['type']):
                case 'image/jpg':
                case 'image/jpeg':
                case 'image/pjpeg':
                    imagejpeg($NewImage, "{$this->cacheFolder}/{$this->imageName}", $this->jpgQuality);
                    break;
                case 'image/png':
                case 'image/x-png':
                    imagepng($NewImage, "{$this->cacheFolder}/{$this->imageName}", $this->pngCompressor);
                    break;
            endswitch;

            if (!$NewImage):
                $this->result = false;
                $this->error = 'Tipo de arquivo inválido, envie imagens JPG ou PNG!';
            else:
                $this->result = "{$this->cacheFolder}/{$this->imageName}";
                $this->error = null;
            endif;

            imagedestroy($this->image);
            imagedestroy($NewImage);
        endif;
    }
}
